import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import ComidaTable from "../tables/ComidaTable"
import ComidaModal from "../modals/ComidaModal"
import Carrito from "./Carrito"
import { NavLink } from "react-router-dom";

const comidasInit=[
    {
        "id":1,
        "nombre":"Milanesa",
        "precio":"TUCULO"
    },
    {
        "id":2,
        "nombre":"Empanada",
        "precio":"TUCULO"
    },
    {
        "id":3,
        "nombre":"Tortilla",
        "precio":"TUCULO"
    }


]

function Comida(props){
    
       const[comidas, setComidas]=useState(comidasInit)
       const[openModal, setOpenModal]=useState(false)
       const[closeModal, setCloseModal]=useState(false)
       const[comidaAgregar, setComidaAgregar]=useState({})
       const [comidaCarrito, setComidaCarrito]= useState([])

      // useEffect(()=> )

       function handleOpenModal(comidaAgregar){
           setOpenModal(true);
           setComidaAgregar(comidaAgregar)
           console.log({comidaAgregar})

       }
       function handleCloseModal(){
        setOpenModal(false);
        setComidaAgregar({})
        console.log(comidaAgregar)

    }


       function handleComida(event, data){
           event.preventDefault();
           handleOpenModal(data);
           console.log("data:"+data) 
       }

       function HandleAgregar(data){
    
           setComidaCarrito([...comidaCarrito, data])
           
            // props.history.push(`/carrito`, {comidaCarrito})
            props=comidaCarrito;
            
           }
           
        

        function handleSubmit(event, data){
            event.preventDefault();
            HandleAgregar(data)
            handleCloseModal()
            
        }
        function agregarComida(){}
            
        
    
    return  (
        <>
        <Button onClick={(data)=>agregarComida}> Agregar comida </Button>
        <ComidaTable 
        data={comidas}
        handleComida={handleComida}
        />
        <ComidaModal
        comidaAgregar={comidaAgregar}
        show={openModal}
        onHide={handleCloseModal}
        handleSubmit={handleSubmit}

        />
        
       
        
        </>
    )
}



export default Comida;
