import {Button, Card} from "react-bootstrap"
//import "../public/app.css"

function TablaBody(props){
    const {
        data
    }=props

   

    

return( 
    data.data.map((item,index)=>{
    return(<>
    
    <div className="col-lg-2 col-md-2 col-sm-6">
            <Card>
               
                    <h2>{item.nombre}</h2>
                    <h3>Precio:${item.precio}</h3>
                    <Button onClick={(e)=>data.handleDelete(e, item.id)}>Eliminar</Button>
                
                
            </Card>
            </div>
        
        
        </>
  
    )

})

)
    }

  
   
   function TablaCarrito(props){
      const {
           comprar, data, CarritoVacio, agregar,pagar
       }=props;

       let total=0;
       for (var i=0; i<data.length; i++){
           total+=data[i].precio
           console.log(data[i].precio)
       }
    
      
      
        return ( 
            <>
            
            
             <CarritoVacio id="carritoVacio"/> 
            
             
             <div className="row">
                 <div className="col-1"></div>
                 <div className="col-10">
                     <div className="row">
                 
                
             <TablaBody data={props} />
             </div>
             </div>
          
             <div className="col-2">
            
             </div>
            
             
             </div>
            
             <Card >
             <h3 id="total" >Total: {total} <Button id="botonCarrito1" onClick={()=>comprar ? pagar():agregar()}>{props.comprar? "Comprar":"Agregar comida"}</Button> </h3>
            
             </Card>
             

            
             
            
            </>)
    }

    export default TablaCarrito;
