//import { Button } from "bootstrap";
import React from "react"
import {Modal, Button,Form, Col} from "react-bootstrap"
import ModalHeader from "react-bootstrap/esm/ModalHeader";
import FormRowSelect from "../FormRow/FormRowSelect"

const ComidaModal=(props)=>{
    const {
        show,
        comidaAgregar,
        onHide,
        handleSubmit,
        isAdd,
        comidas
    }=props;
    const Comida= comidas.map(item=>{
        return <option key={item.id} value={parseInt(item.id)}>{item.nombre} </option>
    })
    let elem=null;
    if (!isAdd){
         elem=<h1> {comidaAgregar.nombre}</h1>;
    } else{
         elem=<Form>
             <FormRowSelect
             placeholder="elija comida"
             value={comidas.Comida.id}
             options={Comida}

             />
         </Form>
    }
    return <Modal 
    show={show}
    onHide={onHide}> 
    <Modal.Header closeButton>
        <Modal.Title>Producto</Modal.Title>

    </Modal.Header>
    <Modal.Body>
        
    {elem}


    </Modal.Body>
    <Modal.Footer>
        <Button variant="danger" onClick={onHide}>Cancelar </Button>
        <Button variant="succes" onClick={(event)=>handleSubmit(event, comidaAgregar)}> {isAdd ?  "Agregar comida" : "Agregar a carrito" }</Button>
    </Modal.Footer>
    
    </Modal>
}

export default ComidaModal;
