//import { Button } from "bootstrap";
import React from "react"
import {Modal, Button,Form, Col} from "react-bootstrap"
import ModalHeader from "react-bootstrap/esm/ModalHeader";


const ComidaModal=(props)=>{
    const {
        show,
        comidasData,
        onHide,
        handleSubmit,
        isAdd,
        comidasList,
        comidaCarrito
    }=props;
    const Comida= comidasList.map(item=>{
        return <option key={item.id} value={parseInt(item.id)} property={item.nombre}>{item.nombre} </option>
    })
    let elem=null;
    if (!isAdd){
         elem=<h1> {comidasData.nombre}</h1>;
    } else{
         elem=<Form>
             <label for="comidas">elije una comida</label>
             <select name="comidas" id="comidas">
             {Comida}
             

            </select> 
         </Form>
    }
    return <Modal 
    show={show}
    onHide={onHide}> 
    <Modal.Header closeButton>
        <Modal.Title>Producto</Modal.Title>

    </Modal.Header>
    <Modal.Body>
        
    {elem}
    <h3>{comidaCarrito.nombre}</h3>


    </Modal.Body>
    <Modal.Footer>
        <Button variant="danger" onClick={onHide}>Cancelar </Button>
        <Button variant="succes" onClick={(event)=>handleSubmit(event, comidasData)}> {isAdd ?  "Agregar comida" : "Agregar a carrito" }</Button>
    </Modal.Footer>
    
    </Modal>
}

export default ComidaModal;
