import {Button, Card} from "react-bootstrap"

function TablaBody(props){
    const {
        data
    }=props

return data.data.map((item,index)=>{
    return(
<div className="col-2">
            <Card>
                <ul className="list-group"> 
                <li className="list-group-item" key={index} >
                    <h2>{item.nombre}</h2>
                    <h3>Precio:${item.precio}</h3>
                    <Button onClick={(event)=> data.handleComida(event, item)}> Ver </Button>
                </li>
                </ul>
                
            </Card>
        </div>
  
    );
})
    }

  
   
   function TablaCarrito(props){
      const {
           comprar, data, Boton, agregar,pagar
       }=props;
    
      // console.log(props)
      //dos opciones, que quede siempre el boton de agregar y que se borre el de comprar cuando esta vacio el carrito, o que se renderice un boton distinto segun este vacio o no. 
      
        return ( 
            <>
            <Boton/>
            <h2><Button onClick={()=>comprar ? pagar():agregar()}>{props.comprar? "Comprar":"Agregar comida"}</Button></h2>
            <TablaBody data={props} />
            
            </>)
    }

    export default TablaCarrito;
