import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import ComidaTable from "../tables/ComidaTable"
import ComidaModal from "../modals/ComidaModal"
import Carrito from "./Carrito"
import { NavLink } from "react-router-dom";
import {Button} from "react-bootstrap"
import { isEnumDeclaration } from "@babel/types";

const comidasInit=[
    {
        "id":1,
        "nombre":"Milanesa",
        "precio":"TUCULO"
    },
    {
        "id":2,
        "nombre":"Empanada",
        "precio":"TUCULO"
    },
    {
        "id":3,
        "nombre":"Tortilla",
        "precio":"TUCULO"
    }


]
const init={
    "id":"",
    "nombre":"",
    "precio":""
}

function Comida(props){
    
       const[comidasList, setComidasList]=useState(comidasInit)
       const[comidasData, setComidasData]=useState({})
       const[openModal, setOpenModal]=useState(false)
       const[closeModal, setCloseModal]=useState(false)
       const[comidaAgregar, setComidaAgregar]=useState({})
       const [comidaCarrito, setComidaCarrito]= useState([])
       const [isAdd, setIsAdd]=useState(false)

      // useEffect(()=> )

       function handleOpenModal(isAdd, data){
           if (isAdd){
               setIsAdd(true);

           }
           setOpenModal(true);
           setComidasData(data)
           console.log(data)

       }
       function handleCloseModal(){
        setOpenModal(false);
        setComidaAgregar({})
        setComidasData({});
        setIsAdd(false);

    }


       function handleComida(event, data){
           event.preventDefault();
           handleOpenModal(false, data);
           console.log("data:"+data) 
       }

       function handleAgregar(data){
    
           setComidaCarrito([...comidaCarrito, data])

           
            // props.history.push(`/carrito`, {comidaCarrito})
            
            
           }
           console.log(comidaCarrito)
           


        function handleSubmit(event, data){
            event.preventDefault();
            setComidaAgregar(data);
            handleAgregar(data)
            handleCloseModal()
            
        }
        function agregarComida(){}

            
        <Carrito
        comidaCarrito={comidaCarrito}
        
        />
    
    return  (
        <>
        <Button onClick={(data)=>handleOpenModal(true, data)}> Agregar comida </Button>
        <ComidaTable 
        data={comidasList}
        handleComida={handleComida}
        comidaCarrito={comidaCarrito}
        />
        <ComidaModal
        comidasData={comidasData}
        show={openModal}
        onHide={handleCloseModal}
        handleSubmit={handleSubmit}
        isAdd={isAdd}
        comidasList={comidasList}
        comidaCarrito={comidaCarrito}

        />
        
       
        
        </>
    )
}



export default Comida;
