//import { Button } from "bootstrap";
import React from "react"
import {Modal, Button} from "react-bootstrap"
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ComidaModal=(props)=>{
    const {
        show,
        comidaAgregar,
        onHide,
        handleSubmit,
        isAdd
    }=props;
    if (!isAdd){
        let elem=<h1> {comidaAgregar.nombre}</h1>;
    } else{
        let elem=<form>

        </form>
    }
    return <Modal 
    show={show}
    onHide={onHide}> 
    <Modal.Header closeButton>
        <Modal.Title>Producto</Modal.Title>

    </Modal.Header>
    <Modal.Body>
        
    {elem}


    </Modal.Body>
    <Modal.Footer>
        <Button variant="danger" onClick={onHide}>Cancelar </Button>
        <Button variant="succes" onClick={(event)=>handleSubmit(event, comidaAgregar)}> {isAdd ?  "Agregar comida" : "Agregar a carrito" }</Button>
    </Modal.Footer>
    
    </Modal>
}

export default ComidaModal;
