import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import ComidaTable from "../tables/ComidaTable"
import ComidaModal from "../modals/ComidaModal"
import Carrito from "./Carrito"
import { NavLink } from "react-router-dom";
import {Button} from "react-bootstrap"
import axios from "axios"


const init=[{
    "id":"",
    "nombre":"",
    "precio":""
}]


function Comida(props){
    
       const[comidasList, setComidasList]=useState(init)
       const[comidasData, setComidasData]=useState({})
       const[openModal, setOpenModal]=useState(false)
       const[closeModal, setCloseModal]=useState(false)
       const[comidaAgregar, setComidaAgregar]=useState({})
       const [comidaCarrito, setComidaCarrito]= useState([])
       const [isAdd, setIsAdd]=useState(false)

      useEffect(async()=>{
          await getComida()//init
      },[] )

      const getComida=async()=>{
          const data=await axios .get("http://localhost:8080/api/comidas")
          .then (response => {
            console.log(response.data)
            setComidasList(response.data)
            //console.log(data.data)
        }).catch(error => {
             console.log(error);
        });
        


      }

       function handleOpenModal(isAdd, data){
           if (isAdd){
               setIsAdd(true);

           }
           setOpenModal(true);
           setComidasData(data)
           console.log(data)

       }
       function handleCloseModal(){
        setOpenModal(false);
        setComidaAgregar({})
        setComidasData({});
        setIsAdd(false);

    }


       function handleComida(event, data){
           event.preventDefault();
           handleOpenModal(false, data);
           console.log("data:"+data) 
       }

       const handleAgregar=(data)=>{

        axios .post("http://localhost:8080/api/carrito", data )
           .then(response=> {
               console.log(response)
           })

    
           //setComidaCarrito([...comidaCarrito, data])
           //CarritoData=[...CarritoData, comidaCarrito]

           
            // props.history.push(`/carrito`, {comidaCarrito})
            
            
           }
           console.log(comidaCarrito)
           


        function handleSubmit(event, data){
            event.preventDefault();
           // setComidaAgregar(data);
            handleAgregar(data)
            handleCloseModal()
            
        }
        function agregarComida(){}

            
        
    
    return  (

        <>
        <Button onClick={(data)=>handleOpenModal(true, data)}> Agregar comida </Button>
        <ComidaTable 
        data={comidasList}
        handleComida={handleComida}
        comidaCarrito={comidaCarrito}
        />
        <ComidaModal
        comidasData={comidasData}
        show={openModal}
        onHide={handleCloseModal}
        handleSubmit={handleSubmit}
        isAdd={isAdd}
        comidasList={comidasList}
        comidaCarrito={comidaCarrito}

        />
       
      
       
        
        </>
    )
}






export default Comida;
