import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import ComidaTable from "../tables/ComidaTable"
import ComidaModal from "../modals/ComidaModal"

const comidasInit=[
    {
        "id":1,
        "nombre":"Milanesa",
        "precio":"TUCULO"
    },
    {
        "id":2,
        "nombre":"Empanada",
        "precio":"TUCULO"
    },
    {
        "id":3,
        "nombre":"Tortilla",
        "precio":"TUCULO"
    }


]

function Comida(){
    
       const[comidas, setComidas]=useState(comidasInit)
       const[openModal, setOpenModal]=useState(false)
       const[closeModal, setCloseModal]=useState(false)
       const[comidaAgregar, setComidaAgregar]=useState({})
       const [comidaCarrito, setComidaCarrito]= useState([])

       function handleOpenModal(comidaAgregar){
           setOpenModal(true);
           setComidaAgregar(comidaAgregar)
           console.log({comidaAgregar})

       }
       function handleCloseModal(){
        setOpenModal(false);
        setComidaAgregar({})

    }


       function handleComida(event, data){
           //event.preventDefault();
           handleOpenModal(data);
           console.log("data:"+data) 
       }

       function HandleAgregar(event, data){
           setComidaCarrito(data)
           console.log("comidaCarrito"+comidaCarrito)
        }
    
    return  (
        <>
        <ComidaTable 
        data={comidas}
        handleComida={handleComida}
        />
        <ComidaModal
        comidaAgregar={comidaAgregar}
        show={openModal}
        onHide={handleCloseModal}
        HandleAgregar={HandleAgregar}

        />
       
        
        </>
    )
}


export default Comida;
