//import { Button } from "bootstrap";
import React from "react"
import {Modal, Button} from "react-bootstrap"
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ComidaModal=(props)=>{
    const {
        show,
        comidaAgregar,
        onHide,
        handleSubmit,
        isAdd
    }=props;
    let elem=null;
    if (!isAdd){
         elem=<h1> {comidaAgregar.nombre}</h1>;
    } else{
         elem=<h1><select name="comidas">
         <option value="nombre">{comidaAgregar.nombre}</option>
         <option value="precio">{comidaAgregar.precio}</option>
       </select></h1>
    }
    return <Modal 
    show={show}
    onHide={onHide}> 
    <Modal.Header closeButton>
        <Modal.Title>Producto</Modal.Title>

    </Modal.Header>
    <Modal.Body>
        
    {elem}


    </Modal.Body>
    <Modal.Footer>
        <Button variant="danger" onClick={onHide}>Cancelar </Button>
        <Button variant="succes" onClick={(event)=>handleSubmit(event, comidaAgregar)}> {isAdd ?  "Agregar comida" : "Agregar a carrito" }</Button>
    </Modal.Footer>
    
    </Modal>
}

export default ComidaModal;
