import React from "react";
import Comida from "./Comida";
import { useState, useEffect } from "react";
import axios from "axios"
import CarritoTable from "../tables/CarritoTable"
import {Button} from "react-bootstrap"
import { NavLink } from "react-router-dom";


const carritoInit=[
    {"id":"",
    "nombre":"",
    "precio":""
}
]

function Carrito(props){

  


        const [dataCarrito, setDataCarrito]=useState({})
        const [carritoList, setCarritoList]=useState(carritoInit)
        const [comprar, setComprar]=useState(false)

        useEffect(async()=>{
            await getCarrito()
            
        },[])


        const getCarrito=async()=>{
             const data=await axios .get("http://localhost:8080/api/carrito")
            .then (response => {
            //console.log(response)
            setCarritoList(response.data)
           // console.log(response)
            if (response.data.length>0){
                setComprar(true)
            }else{
                setComprar(false)
            }
                        //console.log(data.data)
             }).catch(error => {
            console.log(error); })
             } 

        const handleDelete=async(e, id)=>{
            await axios .delete(`http://localhost:8080/api/carrito/${id}`)
            .then (response=>{
                //console.log(response)
                const newData=carritoList.filter(data=>data.id!==id)
                //console.log(newData)
                setCarritoList(newData)
                getCarrito()
                
               
                
            })

        }

        const handleSubmit=(e, data, compra)=>{
            if (compra){
                pagar()
            }else{
                agregarComida()
            }
         }
         const pagar=()=>{

         }
         const agregarComida=()=>{
            props.history.push("/productos/comida")
           
         }

         const Boton=()=>{
             let elem=""
             if (comprar){
                 elem=null;
               
             }else{
                 elem=<h3>El carrito esta vacio</h3>
                
             }
             return elem;
         }
        
             return (


                 <>

                 <h1 align="center" color="red"> Carrito </h1>

                 <CarritoTable
                 data={carritoList}
                 handleDelete={handleDelete}
                 comprar={comprar}
                 Boton={Boton}
                 agregar={agregarComida}
                 pagar={pagar}
                 />
               
                 

                 </>


             )
            
            


    


  

    }

export default Carrito;
